var referrer =  document.referrer;
var cssRules = (document.all) ? 'rules': 'cssRules';
var colors = ['#760CE8', '#4782B1','#da4357','#f00', '#fff', '#fdff00', '#E8890C', '#c4c4c4','#483d8b', '#191970','#006400', '#568203', '#a52a2a', '#ff43a4','#c21e56','darkred','#b8860b', '#3c1874', '#591COB', '#4F4846', '#266150', '#4D774E','#beef00']
var change = colors[Math.floor(Math.random()*colors.length)]
var changeBoxShadow = function(change) {
	const block = document.querySelectorAll(".block");
	const h1 = document.querySelector(".block h1");
	const rld = document.querySelector(".rldvideos");
	const rqbtn = document.querySelector(".requestform button");
	const parentMovieLike = document.querySelector(".parentMovieLike")
	var blocks = Array.from(block)
	var ba = "5px"
	var bb = "5px"
	var bc = "5px"
	var bd = "5px"
	var ra = "-5px"
	var rb = "0px"
	var rc = "5px"
	var rd = "1px"
	blocks.forEach(block => {
		block.style["boxShadow"] =  ba + ' ' + bb + ' ' + bc + ' ' + bd + ' ' + change;
	});
rld.style["boxShadow"] =  ra + ' ' + rb + ' ' + rc + ' ' + rd + ' ' + change;
rqbtn.style.background = change;
parentMovieLike.style["boxShadow"] =  ra + ' ' + rb + ' ' + rc + ' ' + rd + ' ' + change;
h1.style.background = "linear-gradient(180deg," + change + ",#f50101)"
function changeCSSStyle(selector, cssProp, cssVal) {
    var match = false;
	for (i=0, len=document.styleSheets[ssMain][cssRules].length; i<len; i++) {
	 	if (document.styleSheets[ssMain][cssRules][i].selectorText === selector) {
	 	    match = true;
			document.styleSheets[ssMain][cssRules][i].style[cssProp] = cssVal;
			if (change === "#fff" || change === "#c4c4c4" || change === "#fdff00") {
				document.styleSheets[ssMain][cssRules][i].style.color = "#000";
				rqbtn.style.background = "#262626";
			}
	 	}
	 	if(i + 1 === len && match === false){
	 	    document.styleSheets[ssMain].insertRule('.activated{background:'+cssVal+';}', 0)
	 	}
	}
}
	changeCSSStyle('.activated', 'background', change);
};
changeBoxShadow(change);
const epiblock = document.querySelectorAll(".epi-stream")
const seastr = document.querySelectorAll(".seas-stream")
var epis = Array.from(epiblock)
