		$(this).text(timeAgo);
		$(this).removeClass('timeago')
	})
	$array = $('.userlikes').toArray();
	$array_new = $array.slice(-10)
	$array_added = $firstlikes.concat($array_new);
	$loadcnt = parseInt($("#loadcnt").val());
	$($array_added).each(function(i, obj) {
			if ($array.length === $loadcnt * 2) {
				$("#loadmore").remove();
			}	
		$(this).on('click', (e) => {
		var liketype = $(this).attr("data-id");
		var cmtid = $(this).siblings("input").val();
		$clicked_btn = $(this);
		$.ajax({
		type:'POST',
		url: 'includes/likes.php',
		data: {liketype:liketype,
				cmtid:cmtid
		},
		success: function(data){
			if (JSON.parse(data) !== "" ) {		
			res = JSON.parse(data);	
			$clicked_btn.siblings('span.like').text(res.up);
			$clicked_btn.siblings('span.dislike').text(res.down);
			if ($clicked_btn.html() === "thumb_up") {
				$clicked_btn.toggleClass('likecolor');
				if ($clicked_btn.attr("title") == "unlike") {
					$clicked_btn.attr("title", "I like this");
				}else{
					$clicked_btn.attr('title', 'unlike')
				}
				$clicked_btn.parent().children(".dislikecolor").removeClass("dislikecolor");
			}else if($clicked_btn.html() === "thumb_down"){
				$clicked_btn.toggleClass('dislikecolor');
				$clicked_btn.parent().children(".likecolor").attr("title", "I like this");
				$clicked_btn.parent().children(".likecolor").removeClass("likecolor");
			}	
			}
		}
		});
			})
		})
    }, 1300)
	}
})
